INSERT INTO `pizzas` (`id`, `price`, `description`, `name`, `pic_url`) VALUES (NULL, '6.00', 'Is a typical Neapolitan pizza, made with San Marzano tomatoes, mozzarella cheese, fresh basil, salt, and extra-virgin olive oil.', 'Pizza Margherita', NULL), (NULL, '7.00', 'Traditional Pizza From Italy, Western ...\r\nDiavola is a variety of Italian pizza that is traditionally topped with tomato sauce, mozzarella cheese, spicy salami, and hot chili peppers. Black olives are optional and can be added for extra flavor.', 'Pizza Diavola', NULL), (NULL, '8.00', 'Pepperoni pizza is an American pizza variety which includes one of the country\'s most beloved toppings.', 'Pepperoni pizza', NULL)
